exports.NODE_TYPE = "CALL";

exports.handle = function( leaf, node, root ){
    leaf.type = "FUNCTION";
};

exports.attachChild = function( parent, child ){
    if( child.type === 'IDENTIFIER' ){
	parent.name = child.name;
    } else if( child.type === 'LIST' ) {
        parent.arguments = child.children;
    } else {
	throw new Error( "Unrecognized CALL child: " + child );
    }
};