exports.NODE_TYPE = 'FUNCTION';

exports.handle = function( leaf, node, root ){
    leaf.name = node.name;
    // loop through the params to make them objects
    leaf.params = new Array( node.params.length );
    for( var i = 0, l = node.params.length ; i < l ; ++i ){
	leaf.params[i] = {
	    "name": node.params[i],
	    "type": "*"
	};
    }
};