exports.NODE_TYPE = "PROPERTY_INIT";

exports.handle = function( leaf, node, root ){
    // then turn this into the right attachable object
    node.children[1].leaf.name = node.children[0].value;
    node.children[1].leaf.attachDoc( node.children[0].leaf.docs );
    leaf.defineProperty( null, node.children[1].leaf );
};

exports.attachTo = function( parent, child ){
    parent.copyProperties( child );
};