exports.NODE_TYPE = 'SCRIPT';

exports.initialize = function( leaf, node, root ){
    // install as a module
    if( leaf === root ){
	leaf.type = "MODULE";
	leaf.name = null;
	leaf.filename = node.tokenizer.filename;
	leaf.source = node.tokenizer.source;
    } else {
	leaf.children = [];
	leaf.attachChild = function( child ){
	    leaf.children.push( child );
	};
    }    
};

exports.handle = function( leaf, node, root ){

};