exports.NODE_TYPE = 'SCRIPT';

exports.handle = function( leaf, node, root ){
    // install as a module
    leaf.type = "MODULE";
    leaf.name = null;
    leaf.filename = node.tokenizer.filename;
    leaf.source = node.tokenizer.source;

    // attach all children to this
    for( var i = 0, l = leaf.children.length ; i < l ; ++i ){
	if( typeof leaf.children[i].attachTo === 'function' )
	    leaf.children[i].attachTo( leaf );
    }

    delete(leaf.children);
};