/**
 * Handles @module tags.
 */

exports.handle = function( doc, root ){
    // doc needs a module tag
    if( !doc.module ){
	return;
    }

    // cycle through parents until we find a module
    var parent = doc.parent;
    do {
	if( parent.type === "MODULE" ){
	    parent.name = doc.module[0];
	    doc.parent = parent;
	    break;
	}
    } while( ( parent = parent.parent ) );
};