/**
 * 
 */

exports.translate = function( leaf ){
    if( !leaf.children ){ return false; }

    for( var i = 0, l = leaf.children.length ; i < l ; ++i ){
	if( ( leaf.children[i].typeName === "DOC_COMMENT" ) &&
	    ( !leaf.children[i].attributes.memberOf )
	  ) {
	      // the next child must be a FUNCTION or PROPERTY_INIT
	      var nextSibling = leaf.children[ i + 1 ];
	      if( ( nextSibling.typeName === 'FUNCTION' ) ||
		  ( nextSibling.typeName === 'PROPERTY_INIT' )
		){
		    nextSibling.cjsdoc = leaf.children[i];
		}
	  }
    }

    return false;
};